import React, { useState, useEffect } from "react";
import axios from "axios";
import style from "./upload.module.css"
// import AddData from "../AddData/upload";

const JanT22 = () => {
    // const [isLoggedIn, setIsLoggedIn] = useState(true);
    const [addData, setaddData] = useState([]);
    // const [, setfileUpload] = useState([]);

  
    useEffect( () => {
    axios.get(`http://localhost:4000/tables/getAllData`)
        .then((response) => {
            setaddData(response.data);
            console.log(response);
        })
        .catch((err) => {
            console.log(err);
        });
}, []);


    // const handleLogout = () => {
    //     if (isLoggedIn) {
    //         setIsLoggedIn(false);
    //         // setShowProfile(false);
    //         alert("You have been logged out.");
    //         window.location.href = '/';
    //     }
    // };

    const handleDelete = (id) => {
        alert(`Attention! Your file has been deleted successfully`)
        axios.delete(`http://localhost:4000/files/deleteFile/${id}`)
            .then((response) => {
                console.log(response);
                setaddData(response.data);
            })

            .catch((err) => {
                console.log(err);
            });
    };
    return (
        <>
            <div>
                        <div>
                            <div className={style.File_p}>
                                <table>
                                    <thead>
                                        <tr>
                                        <th>Date</th>
                                        <th>Description</th>
                                        <th>Amount</th>
                                        <th>Status</th>
                                    <th>Category</th>
                                    <th>Actions</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            addData.map(addData => {
                                                const { id, date, description, amount, status, category } = addData
                                                return (
                                                    <tr key={id} >
                                                        <td>{date}</td>
                                                        <td>{description}</td>
                                                        <td>{amount}</td>
                                                        <td>{status}</td>
                                                        <td>{category}</td>
                                                        <td>
                                                            {/* <button onClick={() => handleView(idfiles)}>View File</button> */}
                                                            <button onClick={() => handleDelete(idfiles)}>Delete</button>
                                                        </td>
                                                    </tr>
                                                )
                                            })
                                        }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
            
        </>
    )
};


export default JanT22;
