import React, { useState, useEffect } from "react";
import axios from "axios";
import style from "./upload.module.css"
import AddData from "../AddData/upload";

const JanT22 = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(true);
    const [addData, setaddData] = useState([]);
    // const [, setfileUpload] = useState([]);

    useEffect(() => {
        const userId = localStorage.getItem("idusers")
        axios.get(`http://localhost:4000/tables/getAllData`)
            .then((response) => {
                setaddData(response.data);
                console.log(response);
            })
            .catch((err) => {
                console.log(err);
            });
    }, []);

    const handleLogout = () => {
        if (isLoggedIn) {
            setIsLoggedIn(false);
            // setShowProfile(false);
            alert("You have been logged out.");
            window.location.href = '/';
        }
    };

    const handleDelete = (date) => {
        alert(`Attention! Your file has been deleted successfully`)
        axios.delete(`http://localhost:4000/files/deleteFile/${date}`)
            .then((response) => {
                console.log(response);
                setaddData(response.data);
            })

            .catch((err) => {
                console.log(err);
            });
    };
    return (
        <>
            <div>
                        <div>
                            <div className={style.File_p}>
                                <table>
                                    <thead>
                                        <th>Date</th>
                                        <th>Description</th>
                                        <th>Amount</th>
                                        <th>Status</th>
                                        <th>Category</th>
                                    </thead>
                                    <tbody>
                                        {
                                            addData.map(addData => {
                                                const { date, description, amount, status, category } = addData
                                                return (
                                                    <tr>
                                                        <td>{date}</td>
                                                        <td>{description}</td>
                                                        <td>{amount}</td>
                                                        <td>{status}</td>
                                                        <td>{category}</td>
                                                        <td>
                                                            {/* <button onClick={() => handleView(idfiles)}>View File</button> */}
                                                            <button onClick={() => handleDelete(date)}>Delete</button>
                                                        </td>
                                                    </tr>
                                                )
                                            })
                                        }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
            
        </>
    )
};


export default JanT22;
