import React, { useState, useEffect } from "react";
import axios from "axios";
import style from "./upload.module.css"
// import AddData from "../AddData/upload";


const JanT22 = () => {
    const [addData, setaddData] = useState([]);
    const [showUpload, setShowUpload] = useState(false);
    // add
     const [description, setdescription] = useState("")
    const [date, setdate] = useState("")
    const [amount, setamount] = useState("")
    const [status, setstatus] = useState("")
    const [category, setcategory] = useState("")

    useEffect( () => {
    axios.get(`http://localhost:4000/tables/getAllData`)
        .then((response) => {
            setaddData(response.data);
            console.log(response);
        })
        .catch((err) => {
            console.log(err);
        });
}, []);

    const handleDelete = (id) => {
        alert(`Attention! Your file has been deleted successfully`)
        axios.delete(`http://localhost:4000/tables/deleteFile/${id}`)
            .then((response) => {
                console.log(response);
                setaddData(response.data);
            })

            .catch((err) => {
                console.log(err);
            });
    };

     const handleUploadButtonClick = () => {
        //  setShowUpload(!showUpload);
    const handleUpload = () => {
        if (date === " ") return alert('Enter Date');
        if (description === " ") return alert('Enter Description');
        if (!amount === "") return alert("Enter Amount");
        if (!amount.length === 0) return alert("Amount cannot be zero!")
        if (!status === " ") return alert('Enter Status')
        if (!category === " ") return alert ('Enter Category')

        const body = {
            date,
            description,
            amount,
            status,
            category
        }
        console.log(body)
        axios.post("http://localhost:4000/tables/addNewJant22", body)
            .then(Response => {
            console.log(Response)
                alert("Updated Successfully")
                window.location.reload()
                setdate(" ")
                setdescription(" ")
                setamount(" ")
                setstatus(" ")
                setcategory(" ")
        }).catch(err => {
            console.log(err)
            
        })
    }
       const handleCancel = () => {
    window.location.reload()
         }
         
             return (
        <>
            <div className={style.upload_bar}>
                <h1>Add Transaction</h1>
                <br></br>
                <div className={style.upload_info}>
                    <input  type="date" placeholder="yy-mm-dd" onChange={e => setdate(e.target.value)}  min="2022-01-01" max="2022-01-31" />
                    <br /><br />
                    <input type="text" placeholder="Short Description" onChange={e => setdescription(e.target.value)} />
                    <br /><br />
                    <input type="number" placeholder="Enter Amount" onChange={e => setamount(e.target.value)}/>
                    <br /><br />
                    {/* <input type="" placeholder="Enter Status" onChange={e => setstatus(e.target.value)}/> */}
                    <div>
    <label>
      <input type="radio" value="Income" checked={status === "Income"} onChange={e => setstatus(e.target.value)} />
      Income
    </label>
    <label>
      <input type="radio" value="Expense" checked={status === "Expense"} onChange={e => setstatus(e.target.value)} />
      Expense
    </label>
  </div>
                    <br /><br />
                    <input type="text" placeholder="Enter Category" onChange={e => setcategory(e.target.value)}/>
                    {/* <br /><br /> */}
                    <button onClick={handleUpload}>Add</button>&emsp;&emsp;&emsp;&emsp;
                    <button onClick={handleCancel}>Cancle</button>
                </div>
            </div>
        </>
    )
}


    return (
        <>
            <div>
                        <div>
                    <div className={style.File_p}>
                        <li><a onClick={handleUploadButtonClick}>Upload</a></li>
                                <table>
                                    <thead>
                                        <tr>
                                        <th>Date</th>
                                        <th>Description</th>
                                        <th>Amount</th>
                                        <th>Status</th>
                                    <th>Category</th>
                                    <th>Actions</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            addData.map(addData => {
                                                const { id, date, description, amount, status, category } = addData
                                                return (
                                                    <tr key={id} >
                                                        <td>{date}</td>
                                                        <td>{description}</td>
                                                        <td>{amount}</td>
                                                        <td>{status}</td>
                                                        <td>{category}</td>
                                                        <td>
                                                            {/* <button onClick={() => handleView(idfiles)}>View File</button> */}
                                                            <button onClick={() => handleDelete(id)}>Delete</button>
                                                        </td>
                                                    </tr>
                                                )
                                            })
                                        }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
            
        </>
    )
};


export default JanT22;
