import axios from "axios"
import { useState } from "react";
import style from "../AddData/Fileupload.module.css";


const AddData = () => {
    const [description, setdescription] = useState("")
    const [date, setdate] = useState("")
    const [amount, setamount] = useState("")
    const [status, setstatus] = useState("")
    const [category, setcategory] = useState("")
    

    const handleUpload = () => {
     if (!date) {
  alert('Enter Date');
  return;
}

if (!description) {
  alert('Enter Description');
  return;
}

if (!/^\d+$/.test(amount)) {
  alert('Enter only numbers');
  return;
}

if (amount === " " || amount <= 0) {
  alert("Amount cannot be zero or null!");
  return;
}

if (status !== "Expense" && status !== "Income" && status !== " ") {
  alert("Invalid Status!");
  return;
}

if (!category) {
  alert("Enter Category");
  return;
}

        const body = {
            date,
            description,
            amount,
            status,
            category
        }
        console.log(body)
        axios.post("http://localhost:4000/files/addNewFile", body)
            .then(res => {
            console.log(res)
                alert("Updated Successfully")
                window.location.reload()
                setdate("  ")
                setdescription("  ")
                setamount(" ")
                setstatus(" ")
                setcategory(" ")
        }).catch(err => {
            console.log(err)
            
        })
    }

    const handleCancle = () => {
        window.location.reload()
    }
 
    return (
        <>
            <div className={style.upload_bar}>
                <h1>Add Transaction</h1>
                <br></br>
                <div className={style.upload_info}>
                    <input type="date" placeholder="Enter the Date" onChange={e => setdate(e.target.value.split('-').slice(1).join('-'))} />/><br /><br />
                    <input type="text" accept="" placeholder="Short Description" />
                    <br /><br />
                    <input type="number" accept="" placeholder="Enter Income" />
                    <br /><br />
                    <input type="number" accept="" placeholder="Enter Expense" />
                    <br /><br />
                    <button onClick={handleUpload}>Add</button>&emsp;&emsp;&emsp;&emsp;
                    <button onClick={handleCancle}>Cancle</button>
                </div>
            </div>
        </>
    )
}

export default AddData;
