import axios from "axios"
import { useState } from "react";
import style from "../AddData/";


const AddData = () => {
    const [Description, setDescription] = useState("")
    const [Date, setDate] = useState("")
    const [Income, setIncome] = useState([]);
    const [Expense, setExpense] = useState("");

    const handleUpload = () => {
        if (!Date) return alert('Enter Date');
        if (!Description) return alert('Enter Description');
        if (Income === " " || Expense === " ") return alert("Enter the amount in specified category");
        if (Income.length || Expense.length <= 0 )return alert("Amount cannot be zero!")
        const body = {
            Description,
            Date,
            Income,
            Expense,
        }
        console.log(body)
        axios.post("http://localhost:4000/files/addNewFile", body)
            .then(res => {
            console.log(res)
                alert("Updated Successfully")
                window.location.reload()
                setDate("  ")
                setDescription("  ")
                setIncome(" ")
                setExpense(" ")
        }).catch(err => {
            console.log(err)
            
        })
    }

    const handleCancle = () => {
        window.location.reload()
    }
 
    return (
        <>
            <div className={style.upload_bar}>
                <h1>Upload Data</h1>
                <br></br>
                <div className={style.upload_info}>
                    <input type="Date" placeholder="Enter the Date" onChange={e => setDate(e.target.value)}
                    /><br /><br />
                    <input type="text" accept="" placeholder="Short Description" />
                    <br /><br />
                    <input type="number" accept="" placeholder="Enter Income" />
                    <br /><br />
                    <input type="number" accept="" placeholder="Enter Expense" />
                    <br /><br />
                    <button onClick={handleUpload}>Upload</button>&emsp;&emsp;&emsp;&emsp;
                    <button onClick={handleCancle}>Cancle</button>
                </div>
            </div>
        </>
    )
}

export default AddData;
