import React, { useState, useEffect } from "react";
import axios from "axios";
import style from "./upload.module.css"

const JanT22 = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(true);
    const [showUpload, setShowUpload] = useState(false);
    const [fileUpload, setfileUpload] = useState([]);

    const handleLogout = () => {
        if (isLoggedIn) {
            setIsLoggedIn(false);
            // setShowProfile(false);
            alert("You have been logged out.");
            window.location.href = '/';
        }
    };

    const handleView = (idfiles) => {
        window.location.href = `/ViewFile/${idfiles}`;
    };
    const handleDelete = (idfiles) => {
        alert(`Attention! Your file has been deleted successfully`)
        axios.delete(`http://localhost:4000/files/deleteFile/${idfiles}`)
            .then((response) => {
                console.log(response);
                setfileUpload(response.data);
            })

            .catch((err) => {
                console.log(err);
            });
    };
    return (
        <>
            <div>
                        <div>
                            <div className={style.File_p}>
                                <h1 className="Heading"> Banking Files</h1>
                                <table>
                                    <thead>
                                        <th>Sl.No</th>
                                        <th>Date</th>
                                        <th>Description</th>
                                <th>Income</th>
                                <th>Expense</th>
                                    </thead>
                                    <tbody>
                                        {
                                            fileUpload.map(fileUpload => {
                                                const { idfiles, filename } = fileUpload
                                                return (
                                                    <tr>
                                                        <td>{idfiles}</td>
                                                        <td>{filename}</td>
                                                        <td>
                                                            <button onClick={() => handleView(idfiles)}>View File</button>
                                                            <button onClick={() => handleDelete(idfiles)}>Delete</button>
                                                        </td>
                                                    </tr>
                                                )
                                            })
                                        }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
            
        </>
    )
};


export default JanT22;
