import axios from "axios"
import { useState } from "react";
import style from "../AddData/Fileupload.module.css";


const AddData = ({ reload, onCancel }) => {
    const [description, setdescription] = useState("")
    const [date, setdate] = useState("")
    const [amount, setamount] = useState("")
    const [status, setstatus] = useState("")
    const [category, setcategory] = useState("")
    

    useEffect(() => {
    if (reload) {
      window.location.reload();
    }
  }, [reload]);

    const handleUpload = () => {
        if (date === " ") return alert('Enter Date');
        if (description === " ") return alert('Enter Description');
        if (!amount === "") return alert("Enter Amount");
        if (!amount.length === 0) return alert("Amount cannot be zero!")
        if (!status === " ") return alert('Enter Status')
        if (!category === " ") return alert ('Enter Category')

        const body = {
            date,
            description,
            amount,
            status,
            category
        }
        console.log(body)
        axios.post("http://localhost:4000/tables/addNewJant22", body)
            .then(Response => {
            console.log(Response)
                alert("Updated Successfully")
                window.location.reload()
                setdate(" ")
                setdescription(" ")
                setamount(" ")
                setstatus(" ")
                setcategory(" ")
        }).catch(err => {
            console.log(err)
            
        })
    }

    // const handleCancle = () => {
    //     window.location.reload()
    // }
 
    return (
        <>
            <div className={style.upload_bar}>
                <h1>Add Transaction</h1>
                <br></br>
                <div className={style.upload_info}>
                    <input  type="date" placeholder="yy-mm-dd" onChange={e => setdate(e.target.value)} />
                    <br /><br />
                    <input type="text" placeholder="Short Description" onChange={e => setdescription(e.target.value)} />
                    <br /><br />
                    <input type="number" placeholder="Enter Amount" onChange={e => setamount(e.target.value)}/>
                    <br /><br />
                    {/* <input type="" placeholder="Enter Status" onChange={e => setstatus(e.target.value)}/> */}
                    <div>
    <label>
      <input type="radio" value="Income" checked={status === "Income"} onChange={e => setstatus(e.target.value)} />
      Income
    </label>
    <label>
      <input type="radio" value="Expense" checked={status === "Expense"} onChange={e => setstatus(e.target.value)} />
      Expense
    </label>
  </div>
                    <br /><br />
                    <input type="text" placeholder="Enter Category" onChange={e => setcategory(e.target.value)}/>
                    {/* <br /><br /> */}
                    <button onClick={handleUpload}>Add</button>&emsp;&emsp;&emsp;&emsp;
                    <button onClick={onCancel}>Cancle</button>
                </div>
            </div>
        </>
    )
}

export default AddData;
